void lenv_del(lenv*);

void lval_del(lval* v){
    switch(v->type){
        case LVAL_FUN:
            if(!v->builtin){
                lenv_del(v->env);
                lval_del(v->foramls);
                lval_del(v->body);
            }
            break;
        case LVAL_NUM:break;
        case LVAL_ERR:free(v->err);break;
        case LVAL_SYM:free(v->sym);break;
        case LVAL_STR:free(v->str);break;
        case LVAL_QEXPR:
        case LVAL_SEXPR:
            for(int i=0;i<v->count;i++){
                lval_del(v->cell[i]);
            }
            free(v->cell);
            break;
    }
    free(v);
}

void lenv_del(lenv* e){
    for(int i=0;i<e->count;i++){
        free(e->syms[i]);
        lval_del(e->vals[i]);
    }
    free(e->syms);
    free(e->vals);
    free(e);
}

//basic func

int lval_eq(lval* x,lval* y){
    if(x->type!=y->type){
        return 0;
    }

    switch(x->type){
        case LVAL_NUM:return (x->num==y->num);
        case LVAL_ERR:return (strcmp(x->err,y->err)==0);
        case LVAL_SYM:return (strcmp(x->sym,y->sym)==0);
        case LVAL_STR:return (strcmp(x->str,y->str)==0);
        case LVAL_FUN:
            if(x->builtin||y->builtin){
                return x->builtin==y->builtin;
            }else{
                return lval_eq(x->foramls,y->foramls)&&
                    lval_eq(x->body,y->body);
            }
        case LVAL_QEXPR:
        case LVAL_SEXPR:
            if(x->count!=y->count)
                return 0;
            for(int i=0;i<x->count;i++)
                if(!lval_eq(x->cell[i],y->cell[i]))
                    return 0;
            return 1;
        break;
    }
    return 0;
}

lval* lval_read_num(mpc_ast_t* t){
    errno=0;
    double x=strtod(t->contents,NULL);
    return errno!=ERANGE?
        lval_num(x):lval_err("invalid number");
}

//
lval* lval_read_str(mpc_ast_t* t){
    t->contents[strlen(t->contents)-1]='\0';
    char* unescaped=(char*)malloc(strlen(t->contents+1)+1);//1
    strcpy(unescaped,t->contents+1);
    unescaped=(char*)mpcf_unescape(unescaped);
    lval* str=lval_str(unescaped);
    free(unescaped);
    return str;
}

lval* lval_add(lval* v,lval* x){
    v->count++;
    v->cell=(struct lval**)realloc(v->cell,sizeof(lval*)*v->count);
    v->cell[v->count-1]=x;
    return v;
}

lval* lval_read(mpc_ast_t* t){
    if(strstr(t->tag,"number")){return lval_read_num(t);}
    if(strstr(t->tag,"symbol")){return lval_sym(t->contents);}
    if(strstr(t->tag,"string")){return lval_read_str(t);}

    lval* x=NULL;
    //if root (>) or sexpr, create empty list
    if(strcmp(t->tag,">")==0)   {x=lval_sexpr();}
    if(strcmp(t->tag,"sexpr"))  {x=lval_sexpr();}
    if(strstr(t->tag,"qexpr"))  {x=lval_qexpr();}

    //fill the list with any valid expression ontained within
    for(int i=0;i<t->children_num;i++){
        char* content=t->children[i]->contents;
        if(strcmp(content,"(")==0){continue;}
        if(strcmp(content,")")==0){continue;}
        if(strcmp(content,"{")==0){continue;}
        if(strcmp(content,"}")==0){continue;}
        if(strstr(t->children[i]->tag,"comment")){continue;}
        if(strcmp(t->children[i]->tag,"regex")==0){continue;}
        x=lval_add(x,lval_read(t->children[i]));
    }

    return x;
}

