lval* lval_pop(lval* v,int i){
    lval* x=v->cell[i];
    memmove(&v->cell[i],&v->cell[i+1],
            sizeof(lval*)*(v->count-i-1));
    v->count--;
    v->cell=(lval**)realloc(v->cell,sizeof(lval*)*v->count);
    return x;
}

lval* lval_take(lval* v,int i){
    lval* x=lval_pop(v,i);
    lval_del(v);
    return x;
}


lval* lval_eval(lenv* e,lval* v){
    //evaluate sexpressions
    if(v->type==LVAL_SYM){
        lval* x=lenv_get(e,v);
        lval_del(v);
        return x;
    }
    if(v->type==LVAL_SEXPR){return lval_eval_sexpr(e,v);}
    //all other lval types reamin the same
    return v;
}

lval* lval_eval_sexpr(lenv* e,lval* v){

    //evaluate children
    for(int i=0;i<v->count;i++){
        v->cell[i]=lval_eval(e,v->cell[i]);
    }
    //error
    for(int i=0;i<v->count;i++){
        if(v->cell[i]->type==LVAL_ERR){
            return lval_take(v,i);
        }
    }

    //empty
    if(v->count==0){return v;}
    //single
    if(v->count==1){
        return lval_eval(e,lval_take(v,0));
    }

    //function
    lval* f=lval_pop(v,0);
    if(f->type==LVAL_SEXPR){
    	return lval_sexpr();
	}
    if(f->type!=LVAL_FUN){
        lval* err = lval_err(
            "S-Expression starts with incorrect type. "
            "Got %s, Expected %s.",
            ltype_name(f->type), ltype_name(LVAL_FUN));
        lval_del(f);
        lval_del(v);
        return err;
    }

    //call function to get result
    lval* result=lval_call(e,f,v);
    lval_del(f);
    return result;
}

lval* lval_call(lenv* e,lval* f,lval* a){
    if(f->builtin){return f->builtin(e,a);}

    int given=a->count;
    int total=f->foramls->count;

    //arguments
    while(a->count){
        if(f->foramls->count==0){
            lval_del(a);
            return lval_err("Function passed too many arguments."
                "Got %i,Expected %i.",given,total);
        }

        lval* sym=lval_pop(f->foramls,0);
        //
        if(strcmp(sym->sym,"&")==0){
            if(f->foramls->count!=1){
                lval_del(a);
                return lval_err("Function format invalid."
                            "Symbol '&' not followed by single symbol");
            }
            lval* nsym=lval_pop(f->foramls,0);
            lenv_put(f->env,nsym,builtin_list(e,a));
            lval_del(sym);
            lval_del(nsym);
            break;
        }
        //
        lval* val=lval_pop(a,0);
        lenv_put(f->env,sym,val);//bind
        lval_del(sym);
        lval_del(val);
    }
    lval_del(a);
    //
    if(f->foramls->count>0&&
       strcmp(f->foramls->cell[0]->sym,"&")==0){
        if(f->foramls->count!=2){
            return lval_err("Function format invalid."
                        "Symbol '&' not followed by single symbol");
        }

        lval_del(lval_pop(f->foramls,0));
        lval* sym=lval_pop(f->foramls,0);
        lval* val=lval_qexpr();

        lenv_put(f->env,sym,val);
        lval_del(sym);
        lval_del(val);
    }

    if(f->foramls->count==0){
        f->env->par=e;
        return builtin_eval(f->env,
                        lval_add(lval_sexpr(),lval_copy(f->body)));
    }else{
        return lval_copy(f);
    }
}

