lval* builtin_op(lenv* e,lval* a,char* op){
    for(int i=0;i<a->count;i++){
        LASSERT_TYPE(op, a, i, LVAL_NUM);
    }

    lval* x=lval_pop(a,0);
    if((strcmp(op,"-")==0)&&a->count==0){
        x->num=-x->num;
    }

    while(a->count>0){
        lval* y=lval_pop(a,0);
        if(strcmp(op,"+")==0){x->num+=y->num;}
        if(strcmp(op,"-")==0){x->num-=y->num;}
        if(strcmp(op,"*")==0){x->num*=y->num;}
        if(strcmp(op,"/")==0){
            if(y->num==0){
                lval_del(x);
                lval_del(y);
                x=lval_err("Division by zero");
                break;
            }
            x->num/=y->num;
        }
        if(strcmp(op,"^")==0){x->num=pow(x->num,y->num);}
        if(strcmp(op,"%")==0){
            if(y->num==0){
                lval_del(x);
                lval_del(y);
                x=lval_err("Division by zero");
                break;
            }
            x->num=(int)x->num%(int)y->num;
        }
        if(strcmp(op,"#")==0){
            if(y->num==0){
                lval_del(x);
                lval_del(y);
                x=lval_err("Division by zero");
                break;
            }
            x->num=(int)(x->num/y->num);
        }
        lval_del(y);
    }
    lval_del(a);
    return x;
}

