lval* lval_join(lval* x,lval* y){
    for(int i=0;i<y->count;i++){
        x=lval_add(x,y->cell[i]);
    }
    free(y->cell);
    free(y);
    return x;
}

lval* builtin_join(lenv* e,lval* a){
    for(int i=0;i<a->count;i++){
        LASSERT_TYPE("join", a, i, LVAL_QEXPR);
    }

    lval* x=lval_pop(a,0);
    while(a->count){
        lval* y=lval_pop(a,0);
        x=lval_join(x,y);
    }
    lval_del(a);
    return x;
}

