/*logic*/
lval* builtin_ord(lenv* e,lval* a,char* op){
    LASSERT_NUM(op, a, 2);
    LASSERT_TYPE(op, a, 0, LVAL_NUM);
    LASSERT_TYPE(op, a, 1, LVAL_NUM);

    int r;
    if(strcmp(op,">")==0){
        r=(a->cell[0]->num>a->cell[1]->num);
    }
    if(strcmp(op,"<")==0){
        r=(a->cell[0]->num<a->cell[1]->num);
    }
    if(strcmp(op,">=")==0){
        r=(a->cell[0]->num>=a->cell[1]->num);
    }
    if(strcmp(op,"<=")==0){
        r=(a->cell[0]->num<=a->cell[1]->num);
    }
    lval_del(a);
    return lval_num(r);
}

lval* builtin_cmp(lenv* e,lval* a,char* op){
    LASSERT_NUM(op,a,2);
    int r;
    if(strcmp(op,"==")==0){
        r=lval_eq(a->cell[0],a->cell[1]);
    }
    if(strcmp(op,"!=")==0){
        r=!lval_eq(a->cell[0],a->cell[1]);
    }
    lval_del(a);
    return lval_num(r);
}

