lval *readfile(char *path, int *length)
{
	FILE *pfile;
	char *data;
 
	pfile = fopen(path, "rb");
	if (pfile == NULL)
	{
		return lval_err("Unable to open file: %s.",path);
	}
	fseek(pfile, 0, SEEK_END);
	*length = ftell(pfile);
	data = (char *)malloc((*length + 1) * sizeof(char));
	rewind(pfile);
	*length = fread(data, 1, *length, pfile);
	data[*length] = '\0';
	fclose(pfile);
	lval* result=lval_str(data);
	return result;
}

